import React, { useEffect, useState } from 'react'
import axios from 'axios';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
export default function Slider3() {
    let [fproduct, setFproduct]=useState([])
    useEffect(()=>{
axios.get('https://dummyjson.com/products')
  .then(res => {
    console.log(res.data.products);
    setFproduct(res.data.products);
  });

    },[])
    
  return (
    <>
    
    <div className='slider3'>
    <center><h3>Loved By Everyone
    </h3>
    <h4>Favourite Destination</h4>
    <p>Platea rutrum. Enim quam montes Porttitor nam,</p>
    </center>
  

  <OwlCarousel className='owl-theme' loop margin={10} items={4}  nav>
  {fproduct.map((a)=>(
  
<div className="item Slideritem">
<div className=' mini'>
<img src={a.thumbnail}/>
<h3>{a.title}</h3>
<p className='des'>{a.description}</p>

<h5>Price. {a.price}</h5>
<p className='under'></p>

</div>

</div>





))}
  </OwlCarousel>
  
  </div>
      
    </>
  )
}
